import React, { Component } from "react";

class SamDialogue extends Component {
  delaySamDialogue() {
    // alert("i'm delaying");
    setTimeout(
      function() {
        return null;
      }.bind(this),
      3000
    );
    // alert("done delaying");
  }

  render() {
    return (
      <div className="Sam-div">
        <p className="Sam-dialogue">{this.props.dialogue}</p>
      </div>
    );
  }
}

export default SamDialogue;
