import React, { Component } from 'react';

import logo from '../../Assets/logo.svg';
import responseBubble from '../../Assets/response-bubble.png';

import '../../App.css';
import SamDialogue from './SamDialogue';
import UserDialogue from './UserDialogue';
import HeartBar from '../SideBar/HeartBar';
import StarBar from '../SideBar/StarBar';

var month = 0; 
var exchange = 0; 

class MainDialogue extends Component {
  constructor() {
      super()
      this.state={
          isHidden1: true,
          isHidden2: true,
          isHidden3: true,
          dialogue: [
            { id: 'u1a', text: 'Hey!' },
            { id: 'u1b', text: 'Hello' },
            { id: 'u1c', text: 'Bye.' },
          ],
      }
  }
    
  toggleHidden1() {
      this.setState({
          isHidden1: !this.state.isHidden1,
      })
      
      exchange += 1;
  }
  toggleHidden2() {
      this.setState({
          isHidden2: !this.state.isHidden2
      })
  }
  toggleHidden3() {
      this.setState({
          isHidden3: !this.state.isHidden3
      })
  }
  render() {
    return (
        <div>
          <div className="Dialogue-main">
            <div className="Side-bar-container">
              <HeartBar/>
              <StarBar/>
            </div>
            <div className="Box">
              <div className="Box-content" id="Dialogue">
                  <SamDialogue dialogue="Hey Quinn!"/>
                  {!this.state.isHidden1 && exchange==1 && <UserDialogue dialogue="Hey!"/>}
                  {!this.state.isHidden2 && <UserDialogue dialogue="Hello"/>}
                  {!this.state.isHidden3 && <UserDialogue dialogue="Bye"/>}
              </div>
            </div>
          </div>
          <div className="Response-button-row">
            <img src={responseBubble} className="Response-bubble" alt="response bubble" />  
            <div className="Response-text-div">
                <p>Click <br/>to<br/>respond!</p>
            </div>
          </div>
          <div className="Response-row">
            {this.state.dialogue.map((input, index) => {
                return <button className="Response-button" id="Response1" onClick={this.toggleHidden1.bind(this)}>{input.text}</button>
            })}
          </div>
        </div>    
    );
  }
}

function populateResponse() {
//    <button className="Response-button" id="Response1" onClick={this.toggleHidden1.bind(this)}>Hey!</button>
//            <button className="Response-button" id="Response2" onClick={this.toggleHidden2.bind(this)}>Hello</button>
//            <button className="Response-button" id="Response3" onClick={this.toggleHidden3.bind(this)}>Bye</button>
}


export default MainDialogue;
