import React, { Component } from "react";

import "../../App.css";

import MainDialogue from "../Dialogue/MainDialogue";

import heartOn from "../../Assets/heart-on.png";
import heartOff from "../../Assets/heart-off.png";

import starOn from "../../Assets/star-on.png";
import starOff from "../../Assets/star-off.png";

class Intro extends Component {
  constructor(props) {
    super(props);
    this.toggleIntro = this.toggleIntro.bind(this);

    this.state = { currPage: "title" };
  }

  getPageTitle() {
    return (
      <div className="Intro">
        <div>
          <span class="dot" id="currDot" />
          <span class="dot" />
          <span class="dot" />
        </div>
        <div className="Intro-Overlay-Box">
          <div className="Summary-Header">
            <h1>To Be a Friend</h1>
          </div>
          <p>Created by Vi Nguyen | MS Digital Media 2018 - 2019</p>
          <p>Welcome fam</p>
          <button
            className="Summary-Button"
            onClick={() => this.getStateChange()}
          >
            Next
          </button>
        </div>
      </div>
    );
  }

  getPageIntro() {
    return (
      <div>
        <p>Intro page</p>
        <button
          className="Summary-Button"
          onClick={() => this.getStateChange()}
        >
          Next
        </button>
      </div>
    );
  }

  getPageName() {
    return (
      <div>
        <p>Name Page</p>
        <button
          className="Summary-Button"
          onClick={() => this.toggleIntro(false)}
        >
          Start the Experience
        </button>
      </div>
    );
  }

  getStateChange() {
    if (this.state.currPage === "title") {
      this.setState({
        currPage: "intro"
      });
    } else if (this.state.currPage === "intro") {
      this.setState({
        currPage: "name"
      });
    }
  }

  toggleIntro(introState) {
    this.props.toggleIntro(introState);
  }

  render() {
    return (
      <div className="Intro">
        <div className="Intro-Overlay-Box">
          {this.state.currPage === "title" && this.getPageTitle()}
          {this.state.currPage === "intro" && this.getPageIntro()}
          {this.state.currPage === "name" && this.getPageName()}
        </div>
      </div>
    );
  }
}

export default Intro;
