import React, { Component } from 'react';

import logo from '../../Assets/logo.svg';
import responseBubble from '../../Assets/response-bubble.png';

import '../../App.css';
import SamDialogue from './SamDialogue';
import UserDialogue from './UserDialogue';
import HeartBar from '../SideBar/HeartBar';
import StarBar from '../SideBar/StarBar';

var month = 0; 
var exchange = 0; 

var responseID1 = 'u1a';
var responseID2 = 'u1b';
var responseID3 = 'u1c';

class MainDialogue extends Component {
  constructor() {
      super()
      this.state={
          hideExchange1: true,
          hideExchange2: true,
          hideExchange3: true,
          hideExchange4: true,
          hideExchange5: true,
          hideExchange6: true,
          hideExchange7: true,
          hideExchange8: true,
          hideExchange9: true,
          hideExchange10: true,
          hideExchange11: true,
          hideExchange12: true,
          hideExchange13: true,
          hideExchange14: true,
          hideExchange15: true,
          hideExchange16: true,
          
          exchange1: '',
          exchange2: '',
          exchange3: '',
          exchange4: '',
          exchange5: '',
          exchange6: '',
          exchange7: '',
          exchange8: '',
          exchange9: '',
          exchange10: '',
          exchange11: '',
          exchange12: '',
          exchange13: '',
          exchange14: '',
          exchange15: '',
          exchange16: '',
          
          userDialogue: [
            { id: 'u1a', text: "Hey!" },
            { id: 'u1b', text: "Hello" },
            { id: 'u1c', text: "Bye." },
            { id: 'u2a', text: "Yeah idunno. Amanda made me get one, so here we are." },
            { id: 'u2b', text: "Just wanted to be more social, y'know?." },
            { id: 'u2c', text: "I thought that's what all the cool kids are doing these days o:" },  
            { id: 'u3a', text: "We're doing greatttt. Couldn't be happier..." },
            { id: 'u3b', text: "We're alright I guess." },
            { id: 'u3c', text: "Amanda's been fairly busy with med school. Haven't had much time to hang out recently." },
            { id: 'u3d', text: "I wouldn't say that." },
            { id: 'u3e', text: "Why of COURSE I am. :p But the more the merrier!" },
            { id: 'u3f', text: "Honestly I just wanted to connect with old friends that I don't see anymore. :)" },  
            { id: 'u3g', text: "Just a little bored these days." },  
            { id: 'u3h', text: "I'm alright I guess." },  
            { id: 'u4a', text: "Yeah idunno. Amanda made me get one, so here we are." },
            { id: 'u4b', text: "Just wanted to be more social, y'know?." },
            { id: 'u4c', text: "I thought that's what all the cool kids are doing these days o:" },  
              
          ],
          
      }
  }
    
  populateResponses() {
      if(exchange===1) {
          responseID1 = 'u2a';
          responseID2 = 'u2b';
          responseID3 = 'u2c';
      }  
  }
    
  toggleResponse1() {
      if(exchange === 0) {
        this.setState({
          hideExchange1: false,
          exchange1: 'u1a',
        })
      } if (exchange === 1) {
        this.setState({
          hideExchange2: false,
          exchange1: 'u2a',
        })  
      }
      exchange++;
      this.populateResponses();
  }
  toggleResponse2() {
      if(exchange === 0) {
        this.setState({
          hideExchange1: false,
          exchange1: 'u1b',
        })
      }
      exchange++;
      this.populateResponses();
  }
  toggleResponse3() {
      if(exchange === 0) {
        this.setState({
          hideExchange1: false,
          exchange1: 'u1c',
        })
      }
      exchange++;
      this.populateResponses();
  }
    
  render() {
    return (
        <div>
          <div className="Dialogue-main">
            <div className="Side-bar-container">
              <HeartBar/>
              <StarBar/>
            </div>
            <div className="Box">
              <div className="Box-content" id="Dialogue">
                  <SamDialogue dialogue="Hey Quinn!"/>
        
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1a' && <UserDialogue dialogue="Hey!"/>}
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1b' && <UserDialogue dialogue="Hello"/>}
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1c' && <UserDialogue dialogue="Bye"/>}
        
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1a' && <SamDialogue dialogue="Hi!! Glad to see you're on Bookface! I thought you weren't big on social media?"/>} 
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1b' && <SamDialogue dialogue="Hey. :) Glad to see you're on Bookface! I thought you weren't big on social media?"/>} 
                  {!this.state.hideExchange1 && this.state.exchange1 === 'u1c' && <SamDialogue dialogue="HAHA. That's a good one. We just started talking! Glad to see you're on Bookface though. I thought you weren't big on social media?"/>} 
        
    
              </div>
            </div>
          </div>
          <div className="Response-button-row">
            <img src={responseBubble} className="Response-bubble" alt="response bubble" />  
            <div className="Response-text-div">
                <p>Click <br/>to<br/>respond!</p>
            </div>
          </div>
          <div className="Response-row">
            <button className="Response-button" id="Response1" onClick={this.toggleResponse1.bind(this)}>{this.state.userDialogue.find(x => x.id === responseID1).text}</button>
            <button className="Response-button" id="Response2" onClick={this.toggleResponse2.bind(this)}>{this.state.userDialogue.find(x => x.id === responseID2).text}</button>
            <button className="Response-button" id="Response3" onClick={this.toggleResponse3.bind(this)}>{this.state.userDialogue.find(x => x.id === responseID3).text}</button>
        </div>  
      </div>  
    );
  }
}

export default MainDialogue;
