import React, { Component } from "react";

class SamDialogue extends Component {
  delaySamDialogue() {
    alert("i'm delaying");
    setTimeout(
      function() {
        return (
          <div className="Sam-div">
            <p className="Sam-dialogue">{this.props.dialogue}</p>
          </div>
        );
      }.bind(this),
      3000
    );
    alert("done delaying");
  }

  render() {
    {
      this.delaySamDialogue();
    }
    return "";
  }
}

export default SamDialogue;
