sourcesContent:
import React, { Component } from "react";

import logo from "../../Assets/logo.svg";
import responseBubble from "../../Assets/response-bubble.png";

import "../../App.css";

import Summary from "../Summary/Summary";
import { summaryText } from "../Summary/summaryText";

import SamDialogue from "./SamDialogue";
import { samDialogue } from "./DialogueText/SamText";

import UserDialogue from "./UserDialogue";
import { userDialogue } from "./DialogueText/UserText";

import HeartBar from "../SideBar/HeartBar";
import StarBar from "../SideBar/StarBar";

var month = 0;
var exchange = 1;

var personality = 0;

var debug = "hi";

var responseID1 = "u1a";
var responseID2 = "u1b";
var responseID3 = "u1c";

class MainDialogue extends Component {
  constructor() {
    super();

    this.state = {
      hideExchange1: true,
      hideExchange2: true,
      hideExchange3: true,
      hideExchange4: true,
      hideExchange5: true,
      hideExchange6: true,
      hideExchange7: true,
      hideExchange8: true,
      hideExchange9: true,
      hideExchange10: true,
      hideExchange11: true,
      hideExchange12: true,
      hideExchange13: true,
      hideExchange14: true,
      hideExchange15: true,
      hideExchange16: true,

      exchange1: " ",
      exchange2: " ",
      exchange3: " ",
      exchange4: " ",
      exchange5: " ",
      exchange6: " ",
      exchange7: " ",
      exchange8: " ",
      exchange9: " ",
      exchange10: " ",
      exchange11: " ",
      exchange12: " ",
      exchange13: " ",
      exchange14: " ",
      exchange15: " ",
      exchange16: " ",

      hideSummary0: true,
      hideSummary6: true,
      hideSummary12: true,
      hideSummary18: true,
      hideSummary24: true,

      shownSummary0: false,
      shownSummary6: false,
      shownSummary12: false,
      shownSummary18: false,
      shownSummary24: false,

      summary0: " ",
      summary6: " ",
      summary12: " ",
      summary18: " ",
      summary24: " ",

      props: {
        summary: this.getSummaryText,
        retryMonth: this.retryMonth
      }
    };
  }

  getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

  populateResponses(a, b, c) {
    var random = this.getRandomInt(3);

    if (random === 0) {
      responseID1 = a;
      responseID2 = b;
      responseID3 = c;
    } else if (random === 1) {
      responseID1 = c;
      responseID2 = a;
      responseID3 = b;
    } else if (random === 2) {
      responseID1 = b;
      responseID2 = c;
      responseID3 = a;
    }

    exchange++;
  }

  getDateBar(text) {
    return (
      <div className="Date-bar">
        <p>{text}</p>
      </div>
    );
  }

  getUserDialogue(id) {
    return userDialogue.find(x => x.id === id).text;
  }

  getSamDialogue(id) {
    return samDialogue.find(x => x.id === id).text;
  }

  getSummaryText(id) {
    return summaryText.find(x => x.id === id).text;
  }

  changeDialogueState(entry) {
    // *************************************************//
    // ****************** Month 0 **********************//
    // *************************************************//
    if (exchange === 1) {
      if (entry === "u1a") {
        this.setState({
          hideExchange1: false,
          exchange1: "u1a"
        });
        personality++;
      } else if (entry === "u1b") {
        this.setState({
          hideExchange1: false,
          exchange1: "u1b"
        });
        personality++;
      } else if (entry === "u1c") {
        this.setState({
          hideExchange1: false,
          exchange1: "u1c"
        });
      }
      this.populateResponses("u2a", "u2b", "u2c");
    } else if (exchange === 2) {
      if (entry === "u2a") {
        this.setState({
          hideExchange2: false,
          exchange2: "u2a"
        });
        this.populateResponses("u3a", "u3b", "u3c");
      } else if (entry === "u2b") {
        this.setState({
          hideExchange2: false,
          exchange2: "u2b"
        });
        this.populateResponses("u3d", "u3e", "u3c");
      } else if (entry === "u2c") {
        this.setState({
          hideExchange2: false,
          exchange2: "u2c"
        });
        this.populateResponses("u3f", "u3g", "u3h");
      }
    } else if (exchange === 3) {
      if (entry === "u3a") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3a"
        });
        this.populateResponses("u4a", "u4b", "u4c2");
      } else if (entry === "u3b") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3b"
        });
        this.populateResponses("u4a", "u4b", "u4c2");
      } else if (entry === "u3c") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3c"
        });
        this.populateResponses("u4c", "u4d", "u4d2");
      } else if (entry === "u3d") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3d"
        });
        this.populateResponses("u4e", "u4f", "u4g");
      } else if (entry === "u3e") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3e"
        });
        this.populateResponses("u4a", "u4b", "u4c2");
      } else if (entry === "u3f") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3f"
        });
        this.populateResponses("u4h", "u4i", "u4j");
      } else if (entry === "u3g") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3g"
        });
        personality++;
        this.populateResponses("u4h", "u4i", "u4k");
      } else if (entry === "u3h") {
        this.setState({
          hideExchange3: false,
          exchange3: "u3h"
        });
        personality += 2;
        this.populateResponses("u4h", "u4i", "u4l");
      }
    } else if (exchange === 4) {
      if (entry === "u4a") {
        this.setState({
          hideExchange4: false,
          exchange4: "u4a"
        });
      } else if (entry === "u4b") {
        this.setState({
          hideExchange4: false,
          exchange4: "u4b"
        });
      } else if (entry === "u4c") {
        this.setState({
          hideExchange4: false,
          exchange4: "u4c"
        });
