  continueMonth = month => {
    if (month === 0) {
      this.setState({
        hideSummary0: true,
        shownSummary0: true
      });
      month = 6;

      responseID1 = "u7a";
      responseID2 = "u7b";
      responseID3 = "u7c";
    } else if (month === 6) {
      this.setState({
        hideSummary6: true,
        shownSummary6: true
      });
      month = 12;
    } else if (month === 12) {
      this.setState({
        hideSummary12: true,
        shownSummary12: true
      });
      month = 18;
    } else if (month === 18) {
