import React, { Component } from "react";

import logo from "../../Assets/logo.svg";
import responseBubble from "../../Assets/response-bubble.png";

import "../../App.css";

import Summary from "../Summary/Summary";
import { summaryText } from "../Summary/summaryText";

import SamDialogue from "./SamDialogue";
import { samDialogue } from "./DialogueText/SamText";

import UserDialogue from "./UserDialogue";
import { userDialogue } from "./DialogueText/UserText";

import HeartBar from "../SideBar/HeartBar";
import StarBar from "../SideBar/StarBar";

var month = 0;
var starCount = 3;
var exchange = 1;

var personality = 0;
var askedSuicide = false;
var followDream = false;
var goodEnding = false;

var debug = "hi";

var responseID1 = "u1a";
var responseID2 = "u1b";
var responseID3 = "u1c";

class MainDialogue extends Component {
  constructor() {
    super();

    this.state = {
      hideExchange1: true,
      hideExchange2: true,
      hideExchange3: true,
      hideExchange4: true,
      hideExchange5: true,
      hideExchange6: true,
      hideExchange7: true,
      hideExchange8: true,
      hideExchange9: true,
      hideExchange10: true,
      hideExchange11: true,
      hideExchange12: true,
      hideExchange13: true,
      hideExchange14: true,
      hideExchange15: true,
      hideExchange16: true,
      hideExchange17: true,
      hideExchange18: true,
      hideExchange19: true,
      hideExchange20: true,
      hideExchange21: true,
      hideExchange22: true,
      hideExchange23: true,

      exchange1: " ",
      exchange2: " ",
      exchange3: " ",
      exchange4: " ",
      exchange5: " ",
      exchange6: " ",
      exchange7: " ",
      exchange8: " ",
      exchange9: " ",
      exchange10: " ",
      exchange11: " ",
      exchange12: " ",
      exchange13: " ",
      exchange14: " ",
      exchange15: " ",
      exchange16: " ",
      exchange17: " ",
      exchange18: " ",
      exchange19: " ",
      exchange20: " ",
      exchange21: " ",
      exchange22: " ",
      exchange23: " ",

      hideSummary0: true,
      hideSummary6: true,
      hideSummary12: true,
      hideSummary18: true,
      hideSummary24: true,

      shownSummary0: false,
      shownSummary6: false,
      shownSummary12: false,
      shownSummary18: false,
      shownSummary24: false,

      summary0: " ",
      summary6: " ",
      summary12: " ",
      summary18: " ",
      summary24: " ",

      props: {
        summary: this.getSummaryText,
        retryMonth: this.retryMonth
      }
    };
  }

  getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

  populateResponses(a, b, c) {
    var random = this.getRandomInt(3);

    if (random === 0) {
      responseID1 = a;
      responseID2 = b;
      responseID3 = c;
    } else if (random === 1) {
      responseID1 = c;
      responseID2 = a;
      responseID3 = b;
    } else if (random === 2) {
      responseID1 = b;
      responseID2 = c;
      responseID3 = a;
    }

    exchange++;
  }

  getDateBar(text) {
    return (
      <div className="Date-bar">
        <p>{text}</p>
      </div>
    );
  }

  getUserDialogue(id) {
    return userDialogue.find(x => x.id === id).text;
  }

  getSamDialogue(id) {
    return samDialogue.find(x => x.id === id).text;
  }

