  toggleResponse1() {
    this.changeDialogueState(responseID1);
  }
  toggleResponse2() {
    this.changeDialogueState(responseID2);
  }
  toggleResponse3() {
    this.changeDialogueState(responseID3);
  }

  retryMonth = currMonth => {
    if (currMonth === 0) {
      this.setState({
        hideExchange1: true,
        hideExchange2: true,
        hideExchange3: true,
        hideExchange4: true,
        hideExchange5: true,
        hideExchange6: true,
        hideExchange7: true,

        hideSummary0: true
      });
      exchange = 1;
      month = 0;

      responseID1 = "u1a";
      responseID2 = "u1b";
      responseID3 = "u1c";
    } else if (currMonth === 6) {
      this.setState({
        hideExchange7: true,
        hideExchange8: true,
        hideExchange9: true,
        hideExchange10: true,
        hideExchange11: true,
        hideExchange12: true,
        hideExchange13: true,

        hideSummary6: true
      });
      exchange = 7;
      month = 6;

      responseID1 = "u7a";
      responseID2 = "u7b";
      responseID3 = "u7c";
    } else if (currMonth === 12) {
      this.setState({
        hideExchange13: true,
        hideExchange14: true,
        hideExchange15: true,
        hideExchange16: true,
        hideExchange17: true,

        hideSummary12: true
      });
      exchange = 14;
      month = 6;

      if (followDream) {
        responseID1 = "u14aD";
        responseID2 = "u14bD";
        responseID3 = "u14cD";
      } else {
        responseID1 = "u14aN";
        responseID2 = "u14bN";
        responseID3 = "u14cN";
      }
    } else if (currMonth === 18) {
      this.setState({
        hideExchange17: true,
        hideExchange18: true,
        hideExchange19: true,
        hideExchange20: true,
        hideExchange21: true,

        hideSummary18: true
      });
      exchange = 18;
      month = 12;

      responseID1 = "u18a";
      responseID2 = "u18b";
      responseID3 = " ";
    }
  };

  continueMonth = currMonth => {
    if (currMonth === 0) {
      this.setState({
        hideSummary0: true,
        shownSummary0: true
      });

      month = 6;

      responseID1 = "u7a";
      responseID2 = "u7b";
      responseID3 = "u7c";
    } else if (currMonth === 6) {
      this.setState({
        hideSummary6: true,
        shownSummary6: true
      });
      month = 12;
      if (followDream) {
        responseID1 = "u14aD";
        responseID2 = "u14bD";
        responseID3 = "u14cD";
      } else {
        responseID1 = "u14aN";
