import React, { Component } from "react";

import "../../App.css";

import MainDialogue from "../Dialogue/MainDialogue";

import heartOn from "../../Assets/heart-on.png";
import heartOff from "../../Assets/heart-off.png";

import starOn from "../../Assets/star-on.png";
import starOff from "../../Assets/star-off.png";

var state = "name";

class Intro extends Component {
  constructor(props) {
    super(props);
    this.toggleIntro = this.toggleIntro.bind(this);
  }

  getPageTitle() {
    return (
      <div className="Intro">
        <div className="Intro-Overlay-Box">
          <div className="Summary-Header">
            <h1>To Be a Friend</h1>
          </div>
          <p>Created by Vi Nguyen | MS Digital Media 2018 - 2019</p>
          <div>
            <p>Welcome fam</p>
          </div>
        </div>
      </div>
    );
  }

  getPageIntro() {
    return (
      <div className="Date-bar">
        <p>Intro page</p>
      </div>
    );
  }

  getPageName() {
    return (
      <div className="Date-bar">
        <p>Name Page</p>
      </div>
    );
  }

  getStateChange() {
    if (state === "title") {
      state = "intro";
    } else if (state === "intro") {
      state = "name";
    } else if (state === "name") {
    }
  }

  retryMonth(month) {
    this.props.retryMonth(month);
  }

  continueMonth(month) {
    this.props.continueMonth(month);
  }

  render() {
    return (
      <div className="Intro">
        <div className="Intro-Overlay-Box">
          {state === "title" && this.getPageTitle()}
          {state === "intro" && this.getPageIntro()}
          {state === "name" && this.getPageName()}
        </div>
      </div>
    );
  }
}

export default Intro;
