import React, { Component } from 'react';

import logo from '../../Assets/logo.svg';
import responseBubble from '../../Assets/response-bubble.png';

import '../../App.css';
import SamDialogue from './SamDialogue';
import UserDialogue from './UserDialogue';
import HeartBar from '../SideBar/HeartBar';
import StarBar from '../SideBar/StarBar';

class MainDialogue extends Component {
  render() {
    return (
        <div>
          <div className="Dialogue-main">
            <div className="Side-bar-container">
              <HeartBar/>
              <StarBar/>
            </div>
            <div className="Box">
              <div className="Box-content" id="Dialogue">
                  <SamDialogue dialogue="Hi! This is sample text. Hello hello hi. Hi! This is sample text. Hello hello hi. Hi! This is sample text. Hello hello hi. Hi! This is sample text. Hello hello hi."/>
                
              </div>
            </div>
          </div>
          <div className="Response-button-row">
            <img src={responseBubble} className="Response-bubble" alt="response bubble" />  
            <div className="Response-text-div">
                <p>Click <br/>to<br/>respond!</p>
            </div>
          </div>
          <div className="Response-row">
            <button className="Response-button" id="Response1" onClick={() => responseHandler()}>Hello this is a test reponse hehehhoho </button>
            <button className="Response-button" id="Response2">Hello this is a second test reponse hehe </button>
            <button className="Response-button" id="Response3">Hello this is a third test reponse hehehehoho </button>
            <button className="btn" onClick={this.displayQuestion}>View Unanswered Questions</button>
            {questions}
          </div>
        </div>    
    );
  }
}

function responseHandler() {
    var div = document.getElementById('Dialogue');
    
    var btn = document.createElement('H');
    var t = document.createTextNode('Click me');
    
    btn.appendChild(t);
    
    div.appendChild(btn);
}

var state = {
     questions: [
        { id: 'fdsd', title: 'Why is the sky blue?' },
        { id: 'adsf', title: 'Who invented pizza?' },
        { id: 'afdsf', title: 'Is green tea overrated?' },
     ],
        displayQuestions: false
}

displayQuestion = () => {
    this.setState({
        displayQuestions: !this.state.displayQuestions
    })
}

let questions = null; 

if ( this.state.displayQuestions ) {
     questions = (
     <div>
          { this.state.questions.map((question, index) => {
               return <Question key={question.id}
               title={question.title} />
          })}
     </div>
     )
}

export default MainDialogue;
